import React, { useState, useRef } from "react";
import {
  Box,
  Button,
  VStack,
  HStack,
  Input,
  IconButton,
  Text,
  Alert,
  AlertIcon,
  StackDivider,
  Image,
  Textarea,
  Select,
} from "@chakra-ui/react";
import {
  FaTimes,
  FaWalking,
  FaCar,
  FaBus,
  FaMapMarkerAlt,
} from "react-icons/fa";
import {
  useJsApiLoader,
  GoogleMap,
  Marker,
  InfoWindow,
  DirectionsRenderer,
  Autocomplete,
} from "@react-google-maps/api";
import { appCheck } from "./firebase";

const TravelPlanner = ({
  googleMapsApiKey,
  defaultCenter = { lat: 28.7041, lng: 77.1025 },
}) => {
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey,
    libraries: ["places"],
  });

  const [map, setMap] = useState(null);
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [distance, setDistance] = useState("");
  const [duration, setDuration] = useState("");
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [itineraries, setItineraries] = useState({});
  const originRef = useRef();
  const destinationRef = useRef();
  const [travelMode, setTravelMode] = useState("DRIVING");

  if (!isLoaded) return <Text>Loading...</Text>;

  async function calculateRoute() {
    if (!originRef.current.value || !destinationRef.current.value) {
      setError("Please enter both origin and destination.");
      return;
    }
    try {
      setLoading(true);
      const directionsService = new window.google.maps.DirectionsService();
      const results = await directionsService.route({
        origin: originRef.current.value,
        destination: destinationRef.current.value,
        travelMode: window.google.maps.TravelMode[travelMode],
      });
      setDirectionsResponse(results);
      setDistance(results.routes[0].legs[0].distance.text);
      setDuration(results.routes[0].legs[0].duration.text);
      setError(null);
    } catch (error) {
      setError("Error calculating route: " + error.message);
    } finally {
      setLoading(false);
    }
  }

  function clearRoute() {
    setDirectionsResponse(null);
    setDistance("");
    setDuration("");
    originRef.current.value = "";
    destinationRef.current.value = "";
    setError(null);
  }

  async function findNearbyPlaces() {
    if (!map) {
      setError("Map is not initialized.");
      return;
    }
    setLoading(true);
    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location: map.getCenter(),
      radius: 2000,
      type: ["restaurant", "cafe", "tourist_attraction"],
    };

    try {
      const token = await appCheck.getToken();
      console.log("App Check Token:", token);

      service.nearbySearch(request, (results, status) => {
        if (
          status === window.google.maps.places.PlacesServiceStatus.OK &&
          results
        ) {
          setPlaces(results.slice(0, 5));
          setError(null);
        } else {
          setError("No nearby places found.");
        }
      });
    } catch (error) {
      setError("An error occurred while fetching nearby places.");
    } finally {
      setLoading(false);
    }
  }

  function handlePlaceSelection(event) {
    const selected = places.find(
      (place) => place.place_id === event.target.value
    );
    setSelectedPlace(selected);
    if (map && selected) {
      map.panTo(selected.geometry.location);
    }
  }

  function handleItineraryChange(event, placeId) {
    setItineraries({
      ...itineraries,
      [placeId]: event.target.value,
    });
  }

  return (
    <HStack spacing={4} align="start" h="100vh" w="100vw">
      <VStack
        p={4}
        borderRadius="lg"
        bg="white"
        shadow="md"
        spacing={4}
        w={{ base: "full", md: "400px" }}
        align="stretch"
        overflowY="auto"
        divider={<StackDivider borderColor="gray.200" />}
      >
        <HStack spacing={3}>
          <Autocomplete>
            <Input placeholder="Origin" ref={originRef} />
          </Autocomplete>
          <Autocomplete>
            <Input placeholder="Destination" ref={destinationRef} />
          </Autocomplete>
        </HStack>
        <HStack spacing={3}>
          <Button
            leftIcon={<FaWalking />}
            onClick={() => setTravelMode("WALKING")}
            colorScheme={travelMode === "WALKING" ? "blue" : "gray"}
          >
            Walking
          </Button>
          <Button
            leftIcon={<FaCar />}
            onClick={() => setTravelMode("DRIVING")}
            colorScheme={travelMode === "DRIVING" ? "blue" : "gray"}
          >
            Car
          </Button>
          <Button
            leftIcon={<FaBus />}
            onClick={() => setTravelMode("TRANSIT")}
            colorScheme={travelMode === "TRANSIT" ? "blue" : "gray"}
          >
            Public Transport
          </Button>
        </HStack>
        <HStack spacing={3}>
          <Button
            colorScheme="blue"
            onClick={calculateRoute}
            isLoading={loading}
            w="full"
          >
            Start Journey
          </Button>
          <IconButton
            icon={<FaTimes />}
            onClick={clearRoute}
            colorScheme="red"
            aria-label="Clear route"
          />
        </HStack>
        <Text fontSize="lg" fontWeight="bold">
          Distance: {distance}
        </Text>
        <Text fontSize="lg" fontWeight="bold">
          Duration: {duration}
        </Text>
        <Button
          colorScheme="green"
          onClick={findNearbyPlaces}
          isLoading={loading}
          w="full"
        >
          Find Best Places Nearby
        </Button>

        {error && (
          <Alert status="error">
            <AlertIcon />
            {error}
          </Alert>
        )}

        <VStack spacing={3} align="stretch">
          {places.map((place, index) => (
            <Box
              key={index}
              p={2}
              shadow="xs"
              borderWidth="1px"
              borderRadius="md"
              onClick={() => setSelectedPlace(place)}
              _hover={{ bg: "gray.100", cursor: "pointer" }}
            >
              <HStack>
                <FaMapMarkerAlt color="red" />
                <Text fontWeight="bold">{place.name}</Text>
              </HStack>
              <Text>{place.vicinity}</Text>
            </Box>
          ))}
        </VStack>

        {places.length > 0 && (
          <Select
            placeholder="Select a place"
            onChange={handlePlaceSelection}
            value={selectedPlace ? selectedPlace.place_id : ""}
          >
            {places.map((place, index) => (
              <option key={index} value={place.place_id}>
                {place.name}
              </option>
            ))}
          </Select>
        )}

        {selectedPlace && (
          <Box p={4} borderWidth="1px" borderRadius="md" shadow="sm">
            <Text fontWeight="bold" mb={2}>
              Itinerary for {selectedPlace.name}
            </Text>
            <Text>Address: {selectedPlace.vicinity}</Text>
            <Text>Rating: {selectedPlace.rating || "N/A"}</Text>
            <Textarea
              placeholder="Add your itinerary here..."
              value={itineraries[selectedPlace.place_id] || ""}
              onChange={(event) =>
                handleItineraryChange(event, selectedPlace.place_id)
              }
              mt={3}
            />
            {selectedPlace.photos && selectedPlace.photos.length > 0 && (
              <Image
                src={selectedPlace.photos[0].getUrl()}
                alt={selectedPlace.name}
                borderRadius="md"
                mt={3}
              />
            )}
          </Box>
        )}
      </VStack>

      <Box flex="1" h="100%" position="relative">
        <GoogleMap
          center={defaultCenter}
          zoom={15}
          mapContainerStyle={{ width: "100%", height: "100%" }}
          options={{
            streetViewControl: false,
            fullscreenControl: false,
            mapTypeControl: true,
            mapTypeControlOptions: {
              position: window.google.maps.ControlPosition.TOP_RIGHT,
            },
            zoomControl: true,
            zoomControlOptions: {
              position: window.google.maps.ControlPosition.TOP_RIGHT,
            },
          }}
          onLoad={(map) => setMap(map)}
        >
          <Marker position={defaultCenter} />
          {directionsResponse && (
            <DirectionsRenderer directions={directionsResponse} />
          )}
          {places.map((place, index) => (
            <Marker
              key={index}
              position={place.geometry.location}
              onClick={() => setSelectedPlace(place)}
            >
              {selectedPlace && selectedPlace.place_id === place.place_id && (
                <InfoWindow
                  position={place.geometry.location}
                  onCloseClick={() => setSelectedPlace(null)}
                >
                  <Box>
                    <Text fontWeight="bold">{place.name}</Text>
                    <Text>{place.vicinity}</Text>
                  </Box>
                </InfoWindow>
              )}
            </Marker>
          ))}
        </GoogleMap>
      </Box>
    </HStack>
  );
};

export default TravelPlanner;
