import React, { useEffect, useState } from "react";
import {
  Box,
  VStack,
  HStack,
  Text,
  Image,
  Spinner,
  Alert,
  AlertIcon,
  Button,
} from "@chakra-ui/react";
import { FaMapMarkerAlt } from "react-icons/fa";

const NearbyPlaces = ({ latitude, longitude }) => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [imageErrors, setImageErrors] = useState({});
  const [nextPageToken, setNextPageToken] = useState(null);

  const API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  useEffect(() => {
    if (latitude && longitude) {
      fetchNearbyPlaces();
    }
  }, [latitude, longitude]);

  const fetchNearbyPlaces = async (pageToken = "") => {
    setLoading(true);
    setError(null);

    if (!API_KEY) {
      setError("Google Maps API key is missing. Check your .env file.");
      setLoading(false);
      return;
    }

    const url = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${latitude},${longitude}&radius=5000&type=restaurant&key=${API_KEY}${
      pageToken ? `&pagetoken=${pageToken}` : ""
    }`;
    console.log("Fetching places from:", url);

    try {
      const response = await fetch(url);
      const text = await response.text();
      try {
        const data = JSON.parse(text);
        if (data.status === "OK") {
          setPlaces((prevPlaces) => [...prevPlaces, ...data.results]);
          setNextPageToken(data.next_page_token || null);
        } else {
          throw new Error(
            data.error_message || "Failed to fetch nearby places."
          );
        }
      } catch (jsonError) {
        throw new Error("Invalid JSON response: " + text);
      }
    } catch (error) {
      setError(`An error occurred: ${error.message}`);
      console.error("Fetch Error:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Box textAlign="center" mt={4}>
        <Spinner />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert status="error" mt={4}>
        <AlertIcon />
        {error}
      </Alert>
    );
  }

  return (
    <VStack spacing={3} align="stretch" mt={4}>
      {places.map((place) => (
        <Box
          key={place.place_id}
          p={3}
          shadow="xs"
          borderWidth="1px"
          borderRadius="md"
        >
          <HStack>
            <FaMapMarkerAlt color="red" />
            <Text fontWeight="bold">{place.name}</Text>
          </HStack>
          <Text>{place.vicinity || "Address not available"}</Text>
          <Text>Rating: {place.rating || "N/A"}</Text>
          {place.photos &&
            place.photos.length > 0 &&
            !imageErrors[place.place_id] && (
              <Image
                src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=300&photoreference=${place.photos[0].photo_reference}&key=${API_KEY}`}
                alt={place.name}
                borderRadius="md"
                mt={2}
                onError={() =>
                  setImageErrors((prev) => ({
                    ...prev,
                    [place.place_id]: true,
                  }))
                }
              />
            )}
          {imageErrors[place.place_id] && (
            <Text mt={2}>No photo available</Text>
          )}
        </Box>
      ))}
      {nextPageToken && (
        <Button
          mt={4}
          colorScheme="blue"
          onClick={() => fetchNearbyPlaces(nextPageToken)}
        >
          Load More
        </Button>
      )}
    </VStack>
  );
};

export default NearbyPlaces;
