import React, { useEffect, useState } from "react";
import {
  Box,
  VStack,
  HStack,
  Text,
  Image,
  Spinner,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";
import { FaMapMarkerAlt } from "react-icons/fa";

const NearbyPlaces = ({ latitude, longitude }) => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Replace these with your actual Foursquare API keys
  const CLIENT_ID = "YOUR_CLIENT_ID";
  const CLIENT_SECRET = "YOUR_CLIENT_SECRET";

  useEffect(() => {
    if (latitude && longitude) {
      fetchNearbyPlaces();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [latitude, longitude]);

  const fetchNearbyPlaces = async () => {
    setLoading(true);
    setError(null);

    const version = "20231001"; // YYYYMMDD
    const limit = 10;
    const url = `https://api.foursquare.com/v2/venues/explore?ll=${latitude},${longitude}&client_id=${CLIENT_ID}&client_secret=${CLIENT_SECRET}&v=${version}&limit=${limit}`;

    try {
      const response = await fetch(url);
      const data = await response.json();

      if (data.meta.code === 200) {
        const placesData = data.response.groups[0].items.map(
          (item) => item.venue
        );
        setPlaces(placesData);
      } else {
        setError("Failed to fetch nearby places.");
      }
    } catch (error) {
      setError("An error occurred while fetching nearby places.");
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Box textAlign="center" mt={4}>
        <Spinner />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert status="error" mt={4}>
        <AlertIcon />
        {error}
      </Alert>
    );
  }

  return (
    <VStack spacing={3} align="stretch" mt={4}>
      {places.map((place) => (
        <Box
          key={place.id}
          p={3}
          shadow="xs"
          borderWidth="1px"
          borderRadius="md"
        >
          <HStack>
            <FaMapMarkerAlt color="red" />
            <Text fontWeight="bold">{place.name}</Text>
          </HStack>
          <Text>{place.location.address || "Address not available"}</Text>
          <Text>Category: {place.categories[0]?.name || "N/A"}</Text>
          {place.photos && place.photos.groups.length > 0 && (
            <Image
              src={`${place.photos.groups[0].items[0].prefix}300x300${place.photos.groups[0].items[0].suffix}`}
              alt={place.name}
              borderRadius="md"
              mt={2}
            />
          )}
        </Box>
      ))}
    </VStack>
  );
};

export default NearbyPlaces;
