import React, { useState, useRef } from "react";
import TravelMap from "./TravelMap";
import {
  Box,
  Button,
  VStack,
  HStack,
  Input,
  IconButton,
  Text,
  Spinner,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";
import { FaTimes, FaMapMarkerAlt } from "react-icons/fa";
import {
  useJsApiLoader,
  GoogleMap,
  Marker,
  Autocomplete,
  DirectionsRenderer,
} from "@react-google-maps/api";
import { appCheck } from "./firebase"; // Import Firebase App Check

const center = { lat: 28.7041, lng: 77.1025 }; // Neutral location (intersection of the Equator and Prime Meridian)

function App() {
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY, // Keep API key secure
    libraries: ["places"],
  });

  const [map, setMap] = useState(null);
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [distance, setDistance] = useState("");
  const [duration, setDuration] = useState("");
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const originRef = useRef();
  const destinationRef = useRef();
  const [travelMode, setTravelMode] = useState("DRIVING");

  if (!isLoaded) return <Text>Loading...</Text>;

  async function calculateRoute() {
    if (!originRef.current.value || !destinationRef.current.value) {
      setError("Please enter both origin and destination.");
      return;
    }
    try {
      setLoading(true);
      const directionsService = new window.google.maps.DirectionsService();
      const results = await directionsService.route({
        origin: originRef.current.value,
        destination: destinationRef.current.value,
        travelMode: window.google.maps.TravelMode[travelMode],
      });
      setDirectionsResponse(results);
      setDistance(results.routes[0].legs[0].distance.text);
      setDuration(results.routes[0].legs[0].duration.text);
    } catch (error) {
      setError("Error calculating route: " + error.message);
      console.error("Error calculating route:", error);
    } finally {
      setLoading(false);
    }
  }

  function clearRoute() {
    setDirectionsResponse(null);
    setDistance("");
    setDuration("");
    originRef.current.value = "";
    destinationRef.current.value = "";
    setError(null);
  }

  async function findNearbyPlaces() {
    if (!map) {
      setError("Map is not initialized.");
      return;
    }
    setLoading(true);
    const service = new window.google.maps.places.PlacesService(map);
    const request = {
      location: map.getCenter(),
      radius: 2000,
      type: ["restaurant", "cafe", "tourist_attraction"],
    };

    try {
      // Fetch the App Check token and include it in the request headers
      const token = await appCheck.getToken();
      console.log("App Check Token:", token); // Log the token for debugging

      // Handle the Google Places service call
      service.nearbySearch(request, (results, status) => {
        if (
          status === window.google.maps.places.PlacesServiceStatus.OK &&
          results
        ) {
          setPlaces(results.slice(0, 5)); // Limit to top 5 results
          setError(null);
        } else {
          setError("No nearby places found.");
        }
      });
    } catch (error) {
      setError("An error occurred while fetching nearby places.");
      console.error("Error fetching places:", error);
    } finally {
      setLoading(false);
    }
  }

  return (
    <HStack spacing={4} align="start" h="100vh" w="100vw">
      {/* Control Panel */}
      <VStack
        p={4}
        borderRadius="lg"
        bg="white"
        shadow="md"
        spacing={4}
        w={{ base: "full", md: "400px" }}
        align="stretch"
        maxH="fit-content"
        position="relative"
        zIndex={1}
        overflowY="auto"
      >
        <HStack>
          <Autocomplete>
            <Input placeholder="Origin" ref={originRef} />
          </Autocomplete>
          <Autocomplete>
            <Input placeholder="Destination" ref={destinationRef} />
          </Autocomplete>
        </HStack>
        <HStack>
          <Button
            colorScheme="blue"
            onClick={calculateRoute}
            isLoading={loading}
          >
            Find Route
          </Button>
          <IconButton icon={<FaTimes />} onClick={clearRoute} />
        </HStack>
        <Text>Distance: {distance}</Text>
        <Text>Duration: {duration}</Text>
        <Button
          colorScheme="green"
          onClick={findNearbyPlaces}
          isLoading={loading}
        >
          Find Best Places Nearby
        </Button>

        {/* Error Alert */}
        {error && (
          <Alert status="error">
            <AlertIcon />
            {error}
          </Alert>
        )}

        {/* Nearby Places List */}
        <VStack spacing={3} align="stretch">
          {places.map((place, index) => (
            <Box
              key={index}
              p={2}
              shadow="xs"
              borderWidth="1px"
              borderRadius="md"
            >
              <HStack>
                <FaMapMarkerAlt color="red" />
                <Text fontWeight="bold">{place.name}</Text>
              </HStack>
              <Text>{place.vicinity}</Text>
            </Box>
          ))}
        </VStack>
      </VStack>

      {/* Google Map */}
      <Box
        flex="1"
        h="100%"
        position="relative"
        marginLeft={{ base: "0", md: "400px" }}
      >
        <GoogleMap
          center={center}
          zoom={15}
          mapContainerStyle={{ width: "100%", height: "100%" }}
          options={{
            streetViewControl: false,
            fullscreenControl: false,
            mapTypeControl: true, // Enables map/satellite toggle
            mapTypeControlOptions: {
              position: window.google.maps.ControlPosition.TOP_RIGHT, // Moves map controls to top-right
            },
            zoomControl: true,
            zoomControlOptions: {
              position: window.google.maps.ControlPosition.TOP_RIGHT, // Moves zoom controls to top-right
            },
          }}
          onLoad={(map) => setMap(map)}
        >
          <Marker position={center} />
          {directionsResponse && (
            <DirectionsRenderer directions={directionsResponse} />
          )}
        </GoogleMap>
      </Box>
    </HStack>
  );
}

export default App;
