import React, { useEffect, useState } from "react";
import {
  Box,
  VStack,
  HStack,
  Text,
  Image,
  Spinner,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";
import { FaMapMarkerAlt } from "react-icons/fa";

const NearbyPlaces = ({ latitude, longitude }) => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (latitude && longitude) {
      fetchNearbyPlaces();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [latitude, longitude]);

  const fetchNearbyPlaces = async () => {
    setLoading(true);
    setError(null);

    const API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

    if (!API_KEY) {
      setError("Google Places API key is missing. Check your .env file.");
      setLoading(false);
      return;
    }

    const url = `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${latitude},${longitude}&radius=5000&type=restaurant&key=${API_KEY}`;
    console.log(latitue, longitude);

    try {
      const response = await fetch(url);
      const data = await response.json();

      if (data.status === "OK") {
        setPlaces(data.results);
      } else {
        setError(data.error_message || "Failed to fetch nearby places.");
        console.error("Google Places API Error:", data.error_message); // Log for debugging
      }
    } catch (error) {
      setError("An error occurred while fetching nearby places.");
      console.error("Fetch Error:", error); // Log for debugging
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Box textAlign="center" mt={4}>
        <Spinner />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert status="error" mt={4}>
        <AlertIcon />
        {error}
      </Alert>
    );
  }

  return (
    <VStack spacing={3} align="stretch" mt={4}>
      {places.map((place) => (
        <Box
          key={place.place_id}
          p={3}
          shadow="xs"
          borderWidth="1px"
          borderRadius="md"
        >
          <HStack>
            <FaMapMarkerAlt color="red" />
            <Text fontWeight="bold">{place.name}</Text>
          </HStack>
          <Text>{place.vicinity || "Address not available"}</Text>
          <Text>Rating: {place.rating || "N/A"}</Text>
          {place.photos && place.photos.length > 0 && (
            <Image
              src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=300&photoreference=${place.photos[0].photo_reference}&key=${process.env.REACT_APP_GOOGLE_PLACES_API_KEY}`}
              alt={place.name}
              borderRadius="md"
              mt={2}
              onError={(e) => {
                // Handle image loading errors (e.g., placeholder)
                e.target.onerror = null; // Prevent infinite loop
                e.target.src = "placeholder_image_url.png"; // Replace with your placeholder
              }}
            />
          )}
        </Box>
      ))}
    </VStack>
  );
};

export default NearbyPlaces;
