import React, { useEffect, useState } from "react";
import {
  Box,
  VStack,
  HStack,
  Text,
  Image,
  Spinner,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";
import { FaMapMarkerAlt } from "react-icons/fa";

const NearbyPlaces = ({ latitude, longitude }) => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [imageErrors, setImageErrors] = useState({}); // ✅ Track image errors for each place

  useEffect(() => {
    if (latitude && longitude) {
      fetchNearbyPlaces();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [latitude, longitude]);

  const fetchNearbyPlaces = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `/api/nearby-places?lat=${latitude}&lng=${longitude}`
      );
      const data = await response.json();

      if (response.ok) {
        setPlaces(data.results);
      } else {
        throw new Error(data.error_message || "Failed to fetch nearby places.");
      }
    } catch (error) {
      setError(`An error occurred: ${error.message}`);
      console.error("Fetch Error:", error);
    } finally {
      setLoading(false);
    }
  };

  // ✅ Function to handle image load errors for each place
  const handleImageError = (placeId) => {
    setImageErrors((prevErrors) => ({
      ...prevErrors,
      [placeId]: true, // Mark this place's image as errored
    }));
  };

  if (loading) {
    return (
      <Box textAlign="center" mt={4}>
        <Spinner />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert status="error" mt={4}>
        <AlertIcon />
        {error}
      </Alert>
    );
  }

  return (
    <VStack spacing={3} align="stretch" mt={4}>
      {places.map((place) => (
        <Box
          key={place.place_id}
          p={3}
          shadow="xs"
          borderWidth="1px"
          borderRadius="md"
        >
          <HStack>
            <FaMapMarkerAlt color="red" />
            <Text fontWeight="bold">{place.name}</Text>
          </HStack>
          <Text>{place.vicinity || "Address not available"}</Text>
          <Text>Rating: {place.rating || "N/A"}</Text>
          {place.photos &&
            place.photos.length > 0 &&
            !imageErrors[place.place_id] && ( // ✅ Check individual image error state
              <Image
                src={`https://maps.googleapis.com/maps/api/place/photo?maxwidth=300&photoreference=${place.photos[0].photo_reference}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`}
                alt={place.name}
                borderRadius="md"
                mt={2}
                onError={() => handleImageError(place.place_id)} // ✅ Handle error for this image
              />
            )}
          {imageErrors[place.place_id] && (
            <Text mt={2}>No photo available</Text>
          )}
        </Box>
      ))}
    </VStack>
  );
};

export default NearbyPlaces;
